  --shadow: 0px 4px 6px -1px hsl(215 25% 5% / 0.30), 0px 2px 4px -2px hsl(215 25% 5% / 0.25);
  --shadow-md: 0px 6px 8px -2px hsl(215 25% 5% / 0.35), 0px 4px 6px -2px hsl(215 25% 5% / 0.30);
  --shadow-lg: 0px 10px 15px -3px hsl(215 25% 5% / 0.35), 0px 4px 6px -4px hsl(215 25% 5% / 0.30);
  --shadow-xl: 0px 20px 25px -5px hsl(215 25% 5% / 0.40), 0px 8px 10px -6px hsl(215 25% 5% / 0.30);
